function T = Jac_F_a(r, p, h, dr, dp, dh, xLiDAR, yLiDAR, zLiDAR, PxLGF, axINS, ayINS, azINS)
    T = PxLGF + ayINS*(cos(r)*sin(h) + cos(h)*sin(p)*sin(r)) - azINS*(sin(h)*sin(r) - cos(h)*cos(r)*sin(p)) - zLiDAR*((cos(dh)*sin(dr) + cos(dr)*sin(dh)*sin(dp))*(cos(r)*sin(h) + cos(h)*sin(p)*sin(r)) + cos(h)*cos(p)*(sin(dh)*sin(dr) - cos(dh)*cos(dr)*sin(dp)) + cos(dp)*cos(dr)*(sin(h)*sin(r) - cos(h)*cos(r)*sin(p))) + xLiDAR*(sin(dp)*(sin(h)*sin(r) - cos(h)*cos(r)*sin(p)) - cos(dp)*sin(dh)*(cos(r)*sin(h) + cos(h)*sin(p)*sin(r)) + cos(dh)*cos(dp)*cos(h)*cos(p)) + yLiDAR*((cos(dh)*cos(dr) - sin(dh)*sin(dp)*sin(dr))*(cos(r)*sin(h) + cos(h)*sin(p)*sin(r)) + cos(h)*cos(p)*(cos(dr)*sin(dh) + cos(dh)*sin(dp)*sin(dr)) - cos(dp)*sin(dr)*(sin(h)*sin(r) - cos(h)*cos(r)*sin(p))) + axINS*cos(h)*cos(p);
end