function T = Jac_F_dp(r, p, h, dr, dp, dh, xLiDAR, yLiDAR, zLiDAR, a, b, c)
    T = a*(zLiDAR*(cos(dr)*sin(dp)*(sin(h)*sin(r) - cos(h)*cos(r)*sin(p)) - cos(dp)*cos(dr)*sin(dh)*(cos(r)*sin(h) + cos(h)*sin(p)*sin(r)) + cos(dh)*cos(dp)*cos(dr)*cos(h)*cos(p)) + yLiDAR*(sin(dp)*sin(dr)*(sin(h)*sin(r) - cos(h)*cos(r)*sin(p)) - cos(dp)*sin(dh)*sin(dr)*(cos(r)*sin(h) + cos(h)*sin(p)*sin(r)) + cos(dh)*cos(dp)*cos(h)*cos(p)*sin(dr)) + xLiDAR*(cos(dp)*(sin(h)*sin(r) - cos(h)*cos(r)*sin(p)) + sin(dh)*sin(dp)*(cos(r)*sin(h) + cos(h)*sin(p)*sin(r)) - cos(dh)*cos(h)*cos(p)*sin(dp))) - b*(zLiDAR*(cos(dp)*cos(dr)*sin(dh)*(cos(h)*cos(r) - sin(h)*sin(p)*sin(r)) - cos(dr)*sin(dp)*(cos(h)*sin(r) + cos(r)*sin(h)*sin(p)) + cos(dh)*cos(dp)*cos(dr)*cos(p)*sin(h)) + yLiDAR*(cos(dp)*sin(dh)*sin(dr)*(cos(h)*cos(r) - sin(h)*sin(p)*sin(r)) - sin(dp)*sin(dr)*(cos(h)*sin(r) + cos(r)*sin(h)*sin(p)) + cos(dh)*cos(dp)*cos(p)*sin(dr)*sin(h)) - xLiDAR*(cos(dp)*(cos(h)*sin(r) + cos(r)*sin(h)*sin(p)) + sin(dh)*sin(dp)*(cos(h)*cos(r) - sin(h)*sin(p)*sin(r)) + cos(dh)*cos(p)*sin(dp)*sin(h))) - c*(yLiDAR*(cos(dh)*cos(dp)*sin(dr)*sin(p) + cos(p)*cos(r)*sin(dp)*sin(dr) + cos(dp)*cos(p)*sin(dh)*sin(dr)*sin(r)) - xLiDAR*(cos(dh)*sin(dp)*sin(p) - cos(dp)*cos(p)*cos(r) + cos(p)*sin(dh)*sin(dp)*sin(r)) + zLiDAR*(cos(dh)*cos(dp)*cos(dr)*sin(p) + cos(dr)*cos(p)*cos(r)*sin(dp) + cos(dp)*cos(dr)*cos(p)*sin(dh)*sin(r)));
end