function T = Jac_F_dr(r, p, h, dr, dp, dh, yLiDAR, zLiDAR, a, b, c)
    T = c*(yLiDAR*(sin(p)*(sin(dh)*sin(dr) - cos(dh)*cos(dr)*sin(dp)) - cos(p)*sin(r)*(cos(dh)*sin(dr) + cos(dr)*sin(dh)*sin(dp)) + cos(dp)*cos(dr)*cos(p)*cos(r)) - zLiDAR*(cos(p)*sin(r)*(cos(dh)*cos(dr) - sin(dh)*sin(dp)*sin(dr)) - sin(p)*(cos(dr)*sin(dh) + cos(dh)*sin(dp)*sin(dr)) + cos(dp)*cos(p)*cos(r)*sin(dr))) - a*(yLiDAR*((cos(dh)*sin(dr) + cos(dr)*sin(dh)*sin(dp))*(cos(r)*sin(h) + cos(h)*sin(p)*sin(r)) + cos(h)*cos(p)*(sin(dh)*sin(dr) - cos(dh)*cos(dr)*sin(dp)) + cos(dp)*cos(dr)*(sin(h)*sin(r) - cos(h)*cos(r)*sin(p))) + zLiDAR*((cos(dh)*cos(dr) - sin(dh)*sin(dp)*sin(dr))*(cos(r)*sin(h) + cos(h)*sin(p)*sin(r)) + cos(h)*cos(p)*(cos(dr)*sin(dh) + cos(dh)*sin(dp)*sin(dr)) - cos(dp)*sin(dr)*(sin(h)*sin(r) - cos(h)*cos(r)*sin(p)))) - b*(yLiDAR*((cos(dh)*sin(dr) + cos(dr)*sin(dh)*sin(dp))*(cos(h)*cos(r) - sin(h)*sin(p)*sin(r)) - cos(p)*sin(h)*(sin(dh)*sin(dr) - cos(dh)*cos(dr)*sin(dp)) + cos(dp)*cos(dr)*(cos(h)*sin(r) + cos(r)*sin(h)*sin(p))) - zLiDAR*(cos(p)*sin(h)*(cos(dr)*sin(dh) + cos(dh)*sin(dp)*sin(dr)) - (cos(dh)*cos(dr) - sin(dh)*sin(dp)*sin(dr))*(cos(h)*cos(r) - sin(h)*sin(p)*sin(r)) + cos(dp)*sin(dr)*(cos(h)*sin(r) + cos(r)*sin(h)*sin(p))));
end