function T = Jac_F_heading(r, p, h, dr, dp, dh, xLiDAR, yLiDAR, zLiDAR, a, b, axINS, ayINS, azINS)
    T = - b*(ayINS*(cos(r)*sin(h) + cos(h)*sin(p)*sin(r)) - azINS*(sin(h)*sin(r) - cos(h)*cos(r)*sin(p)) - zLiDAR*((cos(dh)*sin(dr) + cos(dr)*sin(dh)*sin(dp))*(cos(r)*sin(h) + cos(h)*sin(p)*sin(r)) + cos(h)*cos(p)*(sin(dh)*sin(dr) - cos(dh)*cos(dr)*sin(dp)) + cos(dp)*cos(dr)*(sin(h)*sin(r) - cos(h)*cos(r)*sin(p))) + xLiDAR*(sin(dp)*(sin(h)*sin(r) - cos(h)*cos(r)*sin(p)) - cos(dp)*sin(dh)*(cos(r)*sin(h) + cos(h)*sin(p)*sin(r)) + cos(dh)*cos(dp)*cos(h)*cos(p)) + yLiDAR*((cos(dh)*cos(dr) - sin(dh)*sin(dp)*sin(dr))*(cos(r)*sin(h) + cos(h)*sin(p)*sin(r)) + cos(h)*cos(p)*(cos(dr)*sin(dh) + cos(dh)*sin(dp)*sin(dr)) - cos(dp)*sin(dr)*(sin(h)*sin(r) - cos(h)*cos(r)*sin(p))) + axINS*cos(h)*cos(p)) - a*(azINS*(cos(h)*sin(r) + cos(r)*sin(h)*sin(p)) - ayINS*(cos(h)*cos(r) - sin(h)*sin(p)*sin(r)) + zLiDAR*((cos(dh)*sin(dr) + cos(dr)*sin(dh)*sin(dp))*(cos(h)*cos(r) - sin(h)*sin(p)*sin(r)) - cos(p)*sin(h)*(sin(dh)*sin(dr) - cos(dh)*cos(dr)*sin(dp)) + cos(dp)*cos(dr)*(cos(h)*sin(r) + cos(r)*sin(h)*sin(p))) + xLiDAR*(cos(dp)*sin(dh)*(cos(h)*cos(r) - sin(h)*sin(p)*sin(r)) - sin(dp)*(cos(h)*sin(r) + cos(r)*sin(h)*sin(p)) + cos(dh)*cos(dp)*cos(p)*sin(h)) + yLiDAR*(cos(p)*sin(h)*(cos(dr)*sin(dh) + cos(dh)*sin(dp)*sin(dr)) - (cos(dh)*cos(dr) - sin(dh)*sin(dp)*sin(dr))*(cos(h)*cos(r) - sin(h)*sin(p)*sin(r)) + cos(dp)*sin(dr)*(cos(h)*sin(r) + cos(r)*sin(h)*sin(p))) + axINS*cos(p)*sin(h));
end