function T = Jac_F_pitch(r, p, h, dr, dp, dh, xLiDAR, yLiDAR, zLiDAR, a, b, c, axINS, ayINS, azINS)
    T = a*(yLiDAR*(cos(h)*cos(p)*sin(r)*(cos(dh)*cos(dr) - sin(dh)*sin(dp)*sin(dr)) - cos(h)*sin(p)*(cos(dr)*sin(dh) + cos(dh)*sin(dp)*sin(dr)) + cos(dp)*cos(h)*cos(p)*cos(r)*sin(dr)) - xLiDAR*(cos(dh)*cos(dp)*cos(h)*sin(p) + cos(h)*cos(p)*cos(r)*sin(dp) + cos(dp)*cos(h)*cos(p)*sin(dh)*sin(r)) + zLiDAR*(cos(h)*sin(p)*(sin(dh)*sin(dr) - cos(dh)*cos(dr)*sin(dp)) - cos(h)*cos(p)*sin(r)*(cos(dh)*sin(dr) + cos(dr)*sin(dh)*sin(dp)) + cos(dp)*cos(dr)*cos(h)*cos(p)*cos(r)) - axINS*cos(h)*sin(p) + azINS*cos(h)*cos(p)*cos(r) + ayINS*cos(h)*cos(p)*sin(r)) - c*(axINS*cos(p) - zLiDAR*(cos(p)*(sin(dh)*sin(dr) - cos(dh)*cos(dr)*sin(dp)) + sin(p)*sin(r)*(cos(dh)*sin(dr) + cos(dr)*sin(dh)*sin(dp)) - cos(dp)*cos(dr)*cos(r)*sin(p)) - xLiDAR*(cos(r)*sin(dp)*sin(p) - cos(dh)*cos(dp)*cos(p) + cos(dp)*sin(dh)*sin(p)*sin(r)) + yLiDAR*(cos(p)*(cos(dr)*sin(dh) + cos(dh)*sin(dp)*sin(dr)) + sin(p)*sin(r)*(cos(dh)*cos(dr) - sin(dh)*sin(dp)*sin(dr)) + cos(dp)*cos(r)*sin(dr)*sin(p)) + azINS*cos(r)*sin(p) + ayINS*sin(p)*sin(r)) - b*(zLiDAR*(sin(h)*sin(p)*(sin(dh)*sin(dr) - cos(dh)*cos(dr)*sin(dp)) - cos(p)*sin(h)*sin(r)*(cos(dh)*sin(dr) + cos(dr)*sin(dh)*sin(dp)) + cos(dp)*cos(dr)*cos(p)*cos(r)*sin(h)) - xLiDAR*(cos(dh)*cos(dp)*sin(h)*sin(p) + cos(p)*cos(r)*sin(dp)*sin(h) + cos(dp)*cos(p)*sin(dh)*sin(h)*sin(r)) + yLiDAR*(cos(p)*sin(h)*sin(r)*(cos(dh)*cos(dr) - sin(dh)*sin(dp)*sin(dr)) - sin(h)*sin(p)*(cos(dr)*sin(dh) + cos(dh)*sin(dp)*sin(dr)) + cos(dp)*cos(p)*cos(r)*sin(dr)*sin(h)) - axINS*sin(h)*sin(p) + azINS*cos(p)*cos(r)*sin(h) + ayINS*cos(p)*sin(h)*sin(r));
end